mapper.select:
select  from project_measures pm

where

and analysis.component_uuid=#{measureQuery.projectUuid}
and pm.component_uuid in

  #{componentUuid}

select from project_measures pm
inner join projects p on p.project_uuid=analysis.component_uuid and p.uuid=pm.component_uuid where
and pm.component_uuid=#{measureQuery.componentUuid}

select pm.id as id, pm.metric_id as metricId, pm.person_id as personId, pm.value as value
from project_measures pm
inner join snapshots analysis on analysis.uuid = pm.analysis_uuid
where
  pm.component_uuid = #{componentUuid}
  and analysis.uuid = #{analysisUuid}
  and pm.metric_id in #{metricId}
  and pm.person_id is null
